mapper.select:
SELECT COUNT(fk_company_staff) AS cnt
FROM tbl_company_dept_staff
WHERE fk_company_dept = #{fk_company_dept}

with WITH_STAFF_LIST AS ( SELECT A.pk_company_staff, A.fk_company , A.fd_staff_id , A.dept_disp_name , A.fd_staff_pw , A.fd_signup_keycode , A.fd_signup_keycode_ok_yn , A.fd_staff_ai_yn , A.fd_staff_ai_uid , A.fd_dnis , A.fd_staff_level_code , ( SELECT fd_name FROM tbl_code WHERE A.fd_staff_level_code = pk_code ) AS fd_staff_level , A.fd_staff_status_code , ( SELECT fd_name FROM tbl_code WHERE A.fd_staff_status_code = pk_code ) AS fd_staff_status , A.fd_staff_phone , A.fd_staff_email , A.fd_staff_duty , A.fd_staff_response_status_code , ( SELECT fd_name FROM tbl_code WHERE A.fd_staff_response_status_code = pk_code ) AS fd_staff_response_status , A.fd_push_noti_yn , A.fd_push_token , A.fd_login_date , A.fk_writer , A.fd_regdate , A.fk_modifier , A.fd_moddate , A.fd_company_master_yn , A.fd_staff_name , A.fd_staff_mobile FROM tbl_company_staff A WHERE A.fd_staff_level_code != 'A1004' ) SELECT count(*) as count FROM WITH_STAFF_LIST AA WHERE AA.fd_staff_ai_yn = 'N' AND AA.fd_staff_status_code != 'A1103' AND AA.fk_company = #{fk_company}
AND AA.dept_disp_name = "${search.searchString}"
AND AA.fd_staff_name LIKE CONCAT('%',UPPER(#{search.orderType}), '%')

SELECT AA.* FROM WITH_STAFF_LIST AA
AND AA.dept_disp_name = "${search.searchString}"
GROUP BY AA.pk_company_staff -- ORDER BY A.fd_regdate DESC
LIMIT ${search.pageSize}
OFFSET ${(search.page -1) * search.pageSize}

select 
	DISTINCT dept_disp_name 
	from tbl_company_staff where fk_company =#{fk_company} 
and fd_staff_ai_yn = 'N' and fd_staff_status_code !='A1103'and dept_disp_name is not null;

